<?phpclass RegistrationForm extends CFormModel{	public $username;	public $email;	public $password;	public $password_confirm;	/**	 * Declares the validation rules.	 * The rules state that username and password are required,	 * and password needs to be authenticated.	 */	public function rules()	{		return array(			// username and password are required			array('username, email, password, password_confirm', 'required'),			array('username, email, password, password_confirm', 'length', 'max'=>128),			array('password', 'match', 'pattern'=>'/^[\w\d]+$/',            'message'=>'Пароль может содержать только латинские буквы и цифры!'),			array('username', 'match', 'pattern'=>'/^[\w\d]+$/',            'message'=>'Имя пользователя может содержать только латинские буквы и цифры!'),			array('email', 'email'),		);	}	}