<?phpclass RegistrationForm extends CFormModel{	public $username;	public $email;	public $password;	public $password_confirm;	/**	 * Declares the validation rules.	 * The rules state that username and password are required,	 * and password needs to be authenticated.	 */	public function rules()	{		return array(			// username and password are required			array('username, email, password, password_confirm', 'required'),			array('username, email, password, password_confirm', 'length', 'max'=>128),			array('password', 'match', 'pattern'=>'/^[\w\d]{6,}$/',            'message'=>'Пароль может содержать только латинские буквы и цифры и быть не короче 6-ти символов!'),			array('username', 'match', 'pattern'=>'/^[\w\d]{4,}$/',            'message'=>'Имя пользователя может содержать только латинские буквы и цифры и быть не короче 4-х символов!'),			array('email', 'email'),			array('username', 'unique', 'attributeName' => 'username', 'className'=>'Users', 'caseSensitive'=>false,					'message'=>'Такой пользователь уже существует!'),			array('email', 'unique', 'attributeName' => 'email', 'className'=>'Users', 'caseSensitive'=>false,					'message'=>'Такой email уже существует!'),			array('password_confirm', 'compare', 'compareAttribute'=>'password', 'message'=>'Пароли не совпадают!'),		);	}		public function accessRules()	{		return array(			array('allow',  // позволим всем пользователям выполнять любые действия				'users'=>array('*'),			),			array('deny', // аутентифицированным пользователям запретим всё				'users'=>array('@'),			),		);	}		}